import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import Machine from './Machines.js';

class Resource extends Component {

  state = {
    stock: 10
  }

  addStoneTest = (amount) => {
    this.setState({
      stock : this.state.stock + amount
    })
  }


  //miningTest = miningTest.bind(this);


  render() {
    return (
      <div className='resource-btn'>
        <Button variant={this.props.color} onMouseOver={() => this.props.miningTest} onClick={() => this.addStoneTest(1)}>
            {this.props.children}
            <span className="badge badge-light" id="stone-span">{this.state.stock}</span>
        </Button>
      </div>
    )
  }
}

export default Resource;
