import React, {Component, Fragment} from 'react';
import Button from 'react-bootstrap/Button';

mining = () => {
  setInterval(() => {
    this.setState({
      stock : this.state.stock + 1
    })
  }, 1000);
}

class Resource extends Component {

  constructor(props) {
    super(props);
    this.state = {
      stock: 10
    };
    mining = mining.bind(this);




    //this.mining = this.mining.bind(this);
    //this.miningThrottled = throttle(this.mining, 1000);
  }



  /*componentDidMount() {
      this.mining();
  };*/

  addResource = (amount) => {
    this.setState({
      stock : this.state.stock + amount
    })
  }


  render() {
    return (
      <Fragment>
        <Button className='resource-btn' variant={this.props.color} onClick={() => this.addResource(1)}>
            {this.props.children}
            <span className="badge badge-light" id="stone-span">{this.state.stock}</span>
        </Button>
      </Fragment>
    )
  }
}


export default Resource;
