import React, {Component, Fragment} from 'react';
import Button from 'react-bootstrap/Button';

class Resource extends Component {

  /*mining = () => {
    this.props.mining();
  }*/

  constructor(props) {
    super(props);
    this.state = {
      stock: 10
    };
    //miningR = this.props.mining.bind(this);

    //this.mining = this.mining.bind(this);
    //this.miningThrottled = throttle(this.mining, 1000);
  }

  miningR = () => {
    this.props.mining;
  }

  miningRB = this.miningR.bind(this);




  componentDidMount() {
      //this.props.mining();
  };

  addResource = (amount) => {
    this.setState({
      stock : this.state.stock + amount
    })
  }


  render() {
    return (
      <Fragment>
        <Button className='resource-btn' variant={this.props.color} onClick={this.miningRB}>
            {this.props.children}
            <span className="badge badge-light" id="stone-span">{this.state.stock}</span>
        </Button>
      </Fragment>
    )
  }
}


export default Resource;
