import React, {Component} from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import ListGroup from 'react-bootstrap/ListGroup';
import Machine from './Machine'

class MachineList extends Component {

  state = {
    tier1: {
      name: "T1",
      tierSpeed: 1,
      tierCost: {
        stone: 10,
        wood: 10,
      },
    },
  }



  render() {
    return (
      <div className="machine-card">
        <Card bg="secondary" text="dark">
          <Card.Header>
            <Button>
              {this.props.type} Machines
            </Button>
          </Card.Header>

          <ListGroup variant="flush">
            <ListGroup.Item bg="dark"><Machine type={this.props.type} tier={this.state.tier1} tierAmount={this.props.tierAmount} mining={this.props.mining}/></ListGroup.Item>
            <ListGroup.Item><Machine type={this.props.type} tierAmount={this.props.tierAmount} mining={this.props.mining}/></ListGroup.Item>
            <ListGroup.Item>Vestibulum at eros</ListGroup.Item>
          </ListGroup>

        </Card>
      </div>
    )
  }
}
 export default MachineList;
