import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import Badge from 'react-bootstrap/Badge';

class Machine extends Component {

  componentDidMount() {
    if (this.props.type === 'Stone ') {
      this.props.mining(this.props.tier.tierSpeed);
    }
  };

  render() {
    return (
      <div className="machine">
        <p>
        Machine {this.props.tier.name}
        <Button className='craft-btn' disabled>Craft</Button>
        <Badge className='cost-badge' variant="secondary" disabled>{this.props.tier.tierCost.stone}</Badge>
        <Badge className='cost-badge' variant="warning">{this.props.tier.tierCost.wood}</Badge>
        </p>
      </div>
    )
  }
}
 export default Machine;
