import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import MachineList from './MachineList';

const Resource = ({name, resource, color}) => {

  const [stock, setStock] = useState(8);

  /*mining = (amount) => {
    setInterval(() => {
      setStock(stock + amount);
    }, 1000);
  }*/

  return (
    <div className="resource-frag">
      <Button className='resource-btn' variant={color} onClick={() => setStock(stock + 1)}>
          {name}
          <span className="badge badge-light stock-badge" id="stone-span">{stock}</span>
      </Button>

      <MachineList type={resource} stock={stock} />
    </div>
  )

}

export default Resource;
