import React, {Component, Fragment} from 'react';
import Button from 'react-bootstrap/Button';
import throttle from 'lodash.throttle';

class Resource extends Component {

  constructor(props) {
    super(props);
    this.mining = this.mining.bind(this);
    this.miningThrottled = throttle(this.mining, 1000);
  }

  mining = () => {
    while (true) {
      setTimeout(() => {
        this.setState({
          stock : this.state.stock + 1
        })
      }, 1000);
    }
  }




  state = {
    stock: 10
  }

  addStoneTest = (amount) => {
    this.setState({
      stock : this.state.stock + amount
    })
  }


  render() {
    return (
      <Fragment>
        <Button className='resource-btn' variant={this.props.color} onMouseOver={this.miningThrottled}>
            {this.props.children}
            <span className="badge badge-light" id="stone-span">{this.state.stock}</span>
        </Button>
      </Fragment>
    )
  }
}


export default Resource;
