import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import MachineList from './MachineList';

class Resource extends Component {

  state = {
    stock: 10,
    t1Amount: 1,
    t2Amount: 10,
    t3Amount: 100,
  }

  mining = (amount) => {
    setInterval(() => {
      this.setState({
        stock : this.state.stock + amount
      })
    }, 1000);


  }

  addResource = (amount) => {
    this.setState({
      stock : this.state.stock + amount
    })
  }

  render() {
    return (
      <div className="resource-frag">
        <Button className='resource-btn' variant={this.props.color} onClick={() => this.addResource(1)}>
            {this.props.name}
            <span className="badge badge-light stock-badge" id="stone-span">{this.state.stock}</span>
        </Button>

        <MachineList type={this.props.name} tier={1} tierAmount={this.state.t2Amount} mining={this.mining}/>

      </div>
    )
  }
}

export default Resource;
