import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';

class ResourceState extends Component {

  state = {
    stock: 10
  }

  addStoneTest = (amount) => {
    this.setState({
      stock : this.state.stock + amount
    })
  }

  render() {
    return (
      <div>
        <Button variant={this.props.color} onClick={() => this.addStoneTest(1)}>
            {this.props.children}
            <span className="badge badge-light" id="stone-span">{this.state.stock}</span>
        </Button>
      </div>
    )
  }
}

export default ResourceState;
