import React, {Component, Fragment} from 'react';
import Button from 'react-bootstrap/Button';

class Resource extends Component {

  constructor(props) {
    super(props);
    this.state = {
      stock: 10
    }

    //this.mining = this.mining.bind(this);
    //this.miningThrottled = throttle(this.mining, 1000);
  }

  mining = () => {
    setInterval(() => {
      this.setState({
        stock : this.state.stock + 1
      })
    }, 2000);
  }





  addStoneTest = (amount) => {
    this.setState({
      stock : this.state.stock + amount
    })
  }


  render() {
    return (
      <Fragment>
        <Button className='resource-btn' variant={this.props.color} onClick={this.mining}>
            {this.props.children}
            <span className="badge badge-light" id="stone-span">{this.state.stock}</span>
        </Button>
      </Fragment>
    )
  }
}


export default Resource;
