import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  render() {
    return (
      <div className="App">
        <header className="App-header">
        <h1>The Resource Game</h1>

        <div class="resource-bar">
             <button id ="stone-btn" className="btn btn-secondary">
                  Stone <span className="badge badge-light" id="stone-span">0</span>
             </button>

             <button id ="food-btn" class="btn btn-danger">
                  Food <span class="badge badge-light" id="food-span">0</span>
             </button>
             <button id ="wood-btn" class="btn btn-warning">
                  Wood <span class="badge badge-light" id="wood-span">0</span>
             </button>
        </div>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
          <h1>Ma touche perso</h1>
        </header>
      </div>
    );
  }

}

export default App;
