import React from 'react';
import Button from 'react-bootstrap/Button';

const Resource = ({children, stock, resource}) => {

  const stockInfo = stock ? (stock) : ('#');
  //const resourceInfo2 = resource == 'stone' ? ('secondary') : ('primary');
  resourceInfo = (resource) => {
    if (resource == 'stone') {
      return 'secondary';
    }
    else if (resource == 'food') {
      return 'danger';
    }

    else if (resource == 'wood') {
      return 'warning';
    }
  }


  if (children) {
    return (
      <div>
        <Button variant={resourceInfo(resource)}>
            {children}
            <span className="badge badge-light" id="stone-span">{stockInfo}</span>
        </Button>
      </div>
    )
  }

  else {
    return <p>Pas de data</p>;
  }

}

export default Resource;
