import React, {Component} from 'react';
import  ResourceBar from './components/ResourceBar';
import './App.css';

class App extends Component {

  state = {
    title : 'My Resource Game'
  }

  changeTitle = () => {
    let title = prompt('Choisissez un titre :', 'Votre titre...');
    this.setState({
      title: title
    })
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1>{this.state.title}</h1>

          <ResourceBar title={this.state.title}/>

        </header>
      </div>
    );
  }

}

export default App;
