import React, {Component} from 'react';
import Resource from './Resource';

class ResourceBar extends Component {

  state = {
    stock: 2,
    miningTest: () => {
      console.log("Test");
      this.setState({

        stock : this.state.stock + 1
      })
    }
  }

  render() {
    return (
      <div className="resource-bar">
        <Resource state={this.state} stock='5' resource='stone' color='secondary'> Stone </Resource>
        <Resource state={this.state} stock='8' resource='food' color='danger'> Food </Resource>
        <Resource state={this.state} stock='12' resource='wood' color ='warning'> Wood </Resource>
      </div>
    )
  }
}
 export default ResourceBar;
