import React, {Component} from 'react';
import Resource from './Resource';

class ResourceBar extends Component {



  state = {
    stock: 2
  }

  render() {
    return (
      <div className="resource-bar">
        <Resource lestate={this.state} stock='5' resource='stone' color='secondary'> Stone </Resource>
        <Resource lestate={this.state} stock='8' resource='food' color='danger'> Food </Resource>
        <Resource lestate={this.state} stock='12' resource='wood' color ='warning'> Wood </Resource>
      </div>
    )
  }
}
 export default ResourceBar;
