import React, {useEffect, useContext} from 'react';
import Button from 'react-bootstrap/Button';
import Badge from 'react-bootstrap/Badge';

const Machine = ({type, tier}) => {

  useEffect(() => {
    if (type === 'Stone ') {
      //this.props.mining(this.props.tier.speed);
      console.log('boo');
    }
  });

  const stock = useContext(StoneContext);

  return (
    <div className="machine">
      <p>
      Machine {tier.name}
      <Button className='craft-btn' onClick={() => setStock(stock - 10)}>Craft</Button>
      <Badge className='cost-badge' variant="secondary">{tier.cost.stone}</Badge>
      <Badge className='cost-badge' variant="warning">{tier.cost.wood}</Badge>
      </p>
    </div>
  )
}
 export default Machine;
