import React, {Component} from 'react';
import Resource from './Resource';

class ResourceBar extends Component {

  state = {
    stock: 2
  }

  miningTest = () => {
    this.setState({
      stock : this.state.stock + 1
    })
  }

  render() {
    return (
      <div className="resource-bar">
        <Resource miningTest={this.miningTest} stock='5' resource='stone' color='secondary'> Stone </Resource>
        <Resource miningTest={this.miningTest} stock='8' resource='food' color='danger'> Food </Resource>
        <Resource miningTest={this.miningTest} stock='12' resource='wood' color ='warning'> Wood </Resource>
      </div>
    )
  }
}
 export default ResourceBar;
