import React, {Component} from 'react';
import Resource from './Resource';

class ResourceBar extends Component {

  render() {
    return (
      <div className="resource-bar">
        <Resource stock='5' resource='stone' color='secondary' mining={this.props.mining}> Stone </Resource>
        <Resource stock='8' resource='food' color='danger'> Food </Resource>
        <Resource stock='12' resource='wood' color ='warning'> Wood </Resource>
      </div>
    )
  }
}
 export default ResourceBar;
