  };*/


  mining = (inter) => {
    setInterval(() => {
      console.log("Called");
      this.setState({
        stock : this.stock + 1
      })
    }, inter);
  }

  componentDidMount() {
      () => this.mining(1000);
  };

  addResource = (amount) => {
    this.setState({
      stock : this.state.stock + amount
    })
  }


  render() {
    return (
      <Fragment>
        <Button className='resource-btn' variant={this.props.color} onClick={() => this.addResource(1)}>
            {this.props.name}
            <span className="badge badge-light" id="stone-span">{this.state.stock}</span>
        </Button>

        <MachineList mining={this.mining}/>

      </Fragment>
    )
  }
}

export default Resource;
