import React, {Component} from 'react';
import Resource from './Resource';

class ResourceBar extends Component {

  state = {
    title : Morphea
  }

  addStoneTest = () => {
    alert('test');
  }

  changeTitle = () => {
    let title = prompt('Choisissez un titre :', 'Votre titre...');
    this.setState({
      title: title
    })
  }

  render() {
    return (
      <div className="resource-bar">
        <h1 onClick={this.changeTitle}>{this.state.title}</h1>
        <Resource stock='5' onClick={this.addStoneTest}> Stone </Resource>
        <Resource stock='8'></Resource>
        <Resource > Wood </Resource>

        {/*<button id ="stone-btn" className="btn btn-secondary">
            Stone <span className="badge badge-light" id="stone-span">0</span>
        </button>

        <button id ="food-btn" className="btn btn-danger">
            Food <span className="badge badge-light" id="food-span">0</span>
        </button>
        <button id ="wood-btn" className="btn btn-warning">
            Wood <span className="badge badge-light" id="wood-span">0</span>
        </button>*/}
      </div>
    )

  }
}
 export default ResourceBar;
