import React, {Component} from 'react';
import App from './App.js';

mining = () => {
  setInterval(() => {
    this.setState({
      stock : this.state.stock + 1
    })
  }, 1000);
}


class Machine extends Component {

  miningTest = () => {
    this.setState({
      stock : this.state.stock + 1
    })
  }

  componentDidMount() {
      mining();
  };

  render() {
    return (
      <div className="machine">
        <p>Machine</p>
      </div>
    )
  }
}
 export default Machine;
