import React from 'react';
import Button from 'react-bootstrap/Button';
import ResourceBar from './ResourceBar';

const Resource = ({children, stock, resource}) => {

  const stockInfo = stock ? (stock) : ('#');
  const resourceInfo2 = resource === 'stone' ? ('secondary') : ('primary');


  if (children) {
    return (
      <div>
        <Button variant={resourceInfo2} onClick={this.addStoneTest}>
            {children}
            <span className="badge badge-light" id="stone-span">{stockInfo}</span>
        </Button>
      </div>
    )
  }

  else {
    return <p>Pas de data</p>;
  }

}

export default Resource;
