  }*/

  constructor(props) {
    super(props);
    this.state = {
      stock: 10
    };
    this.miningR = mining.bind(this);
    //this.miningR2 = this.miningR.bind(this);


    //this.mining = this.mining.bind(this);
    //this.miningThrottled = throttle(this.mining, 1000);
  };

/*  const mining = function() {
    setInterval(() => {
      console.log("Called");
