        stock : this.stock + 1
      })
    }, 1000);
  }*/



  componentDidMount() {
      //this.props.mining();
  };

  addResource = (amount) => {
    this.setState({
      stock : this.state.stock + amount
    })
  }


  render() {
    return (
      <Fragment>
        <Button className='resource-btn' variant={this.props.color} onClick={this.miningR}>
            {this.props.children.toString()}
            <span className="badge badge-light" id="stone-span">{this.state.stock}</span>
        </Button>
      </Fragment>
    )
  }
}

export {mining};
export default Resource;
